import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import {
  Box, Typography, TextField, Button, Grid, Paper, CircularProgress,
  FormControl, InputLabel, Select, MenuItem, Divider, Chip, Alert
} from '@mui/material';
import { Save as SaveIcon, ArrowBack as ArrowBackIcon } from '@mui/icons-material';

const StudentForm = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const isEditMode = Boolean(id);
  
  const [loading, setLoading] = useState(isEditMode);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [vaccinations, setVaccinations] = useState([]);
  
  const [formData, setFormData] = useState({
    first_name: '',
    last_name: '',
    student_id: '',
    date_of_birth: '',
    grade: '',
    section: ''
  });
  
  useEffect(() => {
    const fetchStudentData = async () => {
      if (isEditMode) {
        try {
          // For demo purposes, simulate API response with mock data
          // In a real app, this would be:
          const response = await axios.get(`http://127.0.0.1:8000/api/students/${id}/`);
          
          // Mock data for demonstration
          const mockResponse = {
            data: {
              id: id,
              first_name: "John",
              last_name: "Doe",
              student_id: "STU" + id,
              date_of_birth: "2010-01-01",
              grade: "5",
              section: "A",
              vaccinations: []
            }
          };
          
          setFormData({
            first_name: mockResponse.data.first_name,
            last_name: mockResponse.data.last_name,
            student_id: mockResponse.data.student_id,
            date_of_birth: mockResponse.data.date_of_birth,
            grade: mockResponse.data.grade,
            section: mockResponse.data.section
          });
          
          if (mockResponse.data.vaccinations) {
            setVaccinations(mockResponse.data.vaccinations);
          }
          
          setLoading(false);
        } catch (error) {
          console.error('Error fetching student:', error);
          setError('Failed to load student data');
          setLoading(false);
        }
      }
    };
    
    fetchStudentData();
  }, [id, isEditMode]);
  
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };
  
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setSaving(true);
    
    // Validate form data
    if (!formData.first_name || !formData.last_name || !formData.student_id || 
        !formData.date_of_birth || !formData.grade || !formData.section) {
      setError('Please fill in all required fields');
      setSaving(false);
      return;
    }
    
    try {
      if (isEditMode) {
        // For demo purposes, simulate successful API call
        // In a real app, this would be:
        await axios.put(`http://127.0.0.1:8000/api/students/${id}/`, formData);
        console.log('Updating student:', formData);
        
        // Simulate API delay
        await new Promise(resolve => setTimeout(resolve, 800));
        
        setSuccess('Student updated successfully');
      } else {
        // For demo purposes, simulate successful API call
        // In a real app, this would be:
        await axios.post('http://127.0.0.1:8000/api/students/', formData);
        console.log('Creating student:', formData);
        
        // Simulate API delay
        await new Promise(resolve => setTimeout(resolve, 800));
        
        setSuccess('Student added successfully');
        // Reset form after successful creation
        if (!isEditMode) {
          setFormData({
            first_name: '',
            last_name: '',
            student_id: '',
            date_of_birth: '',
            grade: '',
            section: ''
          });
        }
      }
      
      // Navigate after a short delay to show success message
      setTimeout(() => {
        navigate('/students');
      }, 1500);
      
    } catch (error) {
      console.error('Error saving student:', error);
      setError(error.response?.data?.detail || 'Failed to save student data');
    } finally {
      setSaving(false);
    }
  };
  
  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '50vh' }}>
        <CircularProgress />
      </Box>
    );
  }
  
  return (
    <Box sx={{ p: 3 }}>
      <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
        <Button
          startIcon={<ArrowBackIcon />}
          onClick={() => navigate('/students')}
          sx={{ mr: 2 }}
        >
          Back
        </Button>
        <Typography variant="h4">
          {isEditMode ? 'Edit Student' : 'Add New Student'}
        </Typography>
      </Box>
      
      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>
      )}
      
      {success && (
        <Alert severity="success" sx={{ mb: 2 }}>{success}</Alert>
      )}
      
      <Paper sx={{ p: 3 }}>
        <form onSubmit={handleSubmit}>
          <Grid container spacing={2}>
            <Grid item xs={12} sm={6}>
              <TextField
                name="first_name"
                label="First Name"
                value={formData.first_name}
                onChange={handleInputChange}
                fullWidth
                required
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                name="last_name"
                label="Last Name"
                value={formData.last_name}
                onChange={handleInputChange}
                fullWidth
                required
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                name="student_id"
                label="Student ID"
                value={formData.student_id}
                onChange={handleInputChange}
                fullWidth
                required
                disabled={isEditMode} // Don't allow editing ID for existing students
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                name="date_of_birth"
                label="Date of Birth"
                type="date"
                value={formData.date_of_birth}
                onChange={handleInputChange}
                fullWidth
                required
                InputLabelProps={{ shrink: true }}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                name="grade"
                label="Grade"
                value={formData.grade}
                onChange={handleInputChange}
                fullWidth
                required
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                name="section"
                label="Section"
                value={formData.section}
                onChange={handleInputChange}
                fullWidth
                required
              />
            </Grid>
            
            <Grid item xs={12}>
              <Button
                type="submit"
                variant="contained"
                color="primary"
                startIcon={<SaveIcon />}
                disabled={saving}
                sx={{ mt: 2 }}
              >
                {saving ? 'Saving...' : (isEditMode ? 'Update Student' : 'Add Student')}
              </Button>
            </Grid>
          </Grid>
        </form>
      </Paper>
      
      {/* Vaccination History (only in Edit mode) */}
      {isEditMode && (
        <Box sx={{ mt: 4 }}>
          <Typography variant="h5" gutterBottom>
            Vaccination History
          </Typography>
          
          <Paper sx={{ p: 3 }}>
            {vaccinations.length === 0 ? (
              <Typography>No vaccination records found for this student.</Typography>
            ) : (
              <Grid container spacing={2}>
                {vaccinations.map((vac) => (
                  <Grid item xs={12} sm={6} md={4} key={vac.id}>
                    <Box sx={{ p: 2, border: '1px solid #eee', borderRadius: 1 }}>
                      <Typography variant="h6">{vac.vaccine_name}</Typography>
                      <Divider sx={{ my: 1 }} />
                      <Typography variant="body2">
                        Administered on: {new Date(vac.date_administered).toLocaleDateString()}
                      </Typography>
                      <Typography variant="body2">
                        Drive date: {new Date(vac.drive_date).toLocaleDateString()}
                      </Typography>
                      <Chip 
                        label="Vaccinated" 
                        color="success" 
                        size="small"
                        sx={{ mt: 1 }}
                      />
                    </Box>
                  </Grid>
                ))}
              </Grid>
            )}
          </Paper>
        </Box>
      )}
    </Box>
  );
};

export default StudentForm;
